    public function getSourceContext(): Source
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Register{% endblock %}

{% block body %}
    <div class=\"container d-flex justify-content-center align-items-center min-vh-100\" style=\"background: #092756;\">
        <div class=\"login p-4 rounded shadow-lg\" style=\"background: rgba(0,0,0,0.7); max-width: 400px; width: 100%;\">
            <h1 class=\"text-center text-white mb-4\">Register</h1>
            
            {% for flash_error in app.flashes('verify_email_error') %}
                <div class=\"alert alert-danger\" role=\"alert\">{{ flash_error }}</div>
            {% endfor %}

            {{ form_errors(registrationForm) }}
            {{ form_start(registrationForm) }}
            
            <div class=\"mb-3\">
                {{ form_label(registrationForm.email, 'Email', {'label_attr': {'class': 'form-label text-white'}}) }}
                {{ form_widget(registrationForm.email, {'attr': {'class': 'form-control'}}) }}
                {{ form_errors(registrationForm.email) }}
            </div>

            <div class=\"mb-3\">
                {{ form_label(registrationForm.plainPassword, 'Password', {'label_attr': {'class': 'form-label text-white'}}) }}
                {{ form_widget(registrationForm.plainPassword, {'attr': {'class': 'form-control'}}) }}
                {{ form_errors(registrationForm.plainPassword) }}
            </div>

            <div class=\"mb-3 form-check\">
                {{ form_widget(registrationForm.agreeTerms, {'attr': {'class': 'form-check-input'}}) }}
                <label class=\"form-check-label text-white\" for=\"agreeTerms\">I agree to the terms</label>
                {{ form_errors(registrationForm.agreeTerms) }}
            </div>
            
            <button type=\"submit\" class=\"btn btn-primary w-100 py-2 mt-3\">Register</button>

            {{ form_end(registrationForm) }}
        </div>
    </div>
{% endblock %}
", "registration/register.html.twig", "/Users/diabiraaly/Desktop/Openclassrooms/OC6/templates/registration/register.html.twig");
